<script>
    $('.map__buttons div').click(function(){
        $('.map__buttons div').removeClass('active');
        $(this).addClass('active');
        $('.map__info-table').removeClass('v1 v2 v3');
        if($('.map__address').hasClass('active')){
            $('.map__info-table').addClass('v1');
            $('.map__info-table span').text('23 Street, Wellington 6011, New Zealand');
        }
        else if($('.map__email').hasClass('active')){
            $('.map__info-table').addClass('v2');
            $('.map__info-table span').text('contact@isama.com');
        }
        else if($('.map__phone').hasClass('active')){
            $('.map__info-table').addClass('v3');
            $('.map__info-table span').text('+64 4 913 9032');
        }
    });
    $('.header__menu-content').hide();
    $('.header__menu-logo').click(function(){
        if($(this).hasClass('active')){
            $(this).removeClass('active');
            $('.header__menu-content').hide();
        }
        else{
            $(this).addClass('active');
            $('.header__menu-content').show();
        }
    });
</script>